import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function InfoCardModal(props) {
	const { selectedMedia } = props;
	const [mediaInfo, setMediaInfo] = useState({});

	const options = {
		method: 'GET',
		url: 'https://utelly-tv-shows-and-movies-availability-v1.p.rapidapi.com/idlookup',
		params: { source_id: { selectedMedia }, source: 'imdb', country: 'ca' },
		headers: {
			'X-RapidAPI-Key': '0020d320c9msh19e61a2ef2d72ebp159d70jsn5c0f4f156d66',
			'X-RapidAPI-Host':
				'utelly-tv-shows-and-movies-availability-v1.p.rapidapi.com',
		},
	};

	console.log(mediaInfo);

	useEffect(() => {
		axios
			.get(`http://www.omdbapi.com/?apikey=719de91d&i=${selectedMedia}`)
			.then((data) => setMediaInfo(data.data));
	}, [selectedMedia]);

	axios.request(options).then((response) => console.log(response.data));

	return (
		<div className="modal-overlay">
			<div className="modal">
				<div className="close-btn">
					<button id="closeBtn" onClick={() => props.setSelectedMedia()}>
						X
					</button>
				</div>
				<div className="modal-img-container">
					<img
						src={mediaInfo.Poster}
						alt={`${mediaInfo.Title} poster`}
						className="modal-img"
					/>
				</div>
				<div className="modal-content">
					<p className="capitalized media-title">
						{mediaInfo?.Type}: {mediaInfo?.Title}
					</p>
					<p className="media-info">
						IMDB Rating:{' '}
						<span className="opacity">{mediaInfo?.imdbRating}</span>
					</p>
					<p className="media-info">
						Release Date: <span className="opacity">{mediaInfo?.Released}</span>
					</p>
					<p className="media-info">
						Genre: <span className="opacity">{mediaInfo?.Genre}</span>
					</p>
					<p className="media-info">
						Language: <span className="opacity">{mediaInfo?.Language}</span>
					</p>
					<p className="media-info">
						Rated: <span className="opacity">{mediaInfo?.Rated}</span>
					</p>
					<p className="media-info">
						Director: <span className="opacity">{mediaInfo?.Director}</span>
					</p>
					<p className="media-info">
						Actors: <span className="opacity">{mediaInfo?.Actors}</span>
					</p>
					<p className="media-info">
						Plot: <span className="opacity">{mediaInfo?.Plot}</span>
					</p>
				</div>
			</div>
		</div>
	);
}
