import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function InfoCardModal(props) {
	const { selectedMedia } = props;
	const [mediaInfo, setMediaInfo] = useState({});
	console.log(mediaInfo);

	useEffect(() => {
		axios
			.get(`http://www.omdbapi.com/?apikey=719de91d&i=${selectedMedia}`)
			.then((data) => setMediaInfo(data.data));
	}, [selectedMedia]);

	return (
		<div className="modal-overlay">
			<div className="modal">
				<img
					src={mediaInfo.Poster}
					alt={`${mediaInfo.Title} poster`}
					className="modal-img"
				/>
				<div className="modal-content">
					<p className="capitalized">
						{mediaInfo?.Type}: <span>{mediaInfo?.Title}</span>
					</p>
					<p>IMDB Rating: 9.0</p>
					<p>Genre: Comedy</p>
				</div>
			</div>
		</div>
	);
}
