import React, { useState } from 'react';
import axios from 'axios';
import debounce from 'lodash.debounce';
import Header from './components/Header';
import Search from './components/Search';
import MovieShow from './components/MovieShow';

function App() {
	// creating a search state to save user input, also passing down as a prop
	const [search, setSearch] = useState('');
	console.log(search);

	// function to handle the search input change and capture user input in search state. Passing as prop
	function handleChange(event) {
		setSearch(event.target.value);
		fetchingData(event.target.value);
	}

	const fetchingData = debounce(async (searchString) => {
		await axios.get(
			`http://www.omdbapi.com/?apikey=719de91d&s=${searchString}`
		);
	}, 500);
	async function fetchData(searchString) {
		const response = await axios.get(
			`http://www.omdbapi.com/?apikey=719de91d&s=${searchString}`
		);
		console.log(response);
	}

	return (
		<>
			<Header />
			<Search search={search} handleChange={handleChange} />
			<section className="ms-container">
				<MovieShow />
				<MovieShow />
				<MovieShow />
				<MovieShow />
			</section>
		</>
	);
}

export default App;
